INSERT INTO `jos_wedding_guestbook` (`id`, `user_id`, `from_name`, `content`, `published`, `created_date`) VALUES
(1, 42, 'Phạm Văn An', '<p>chúc mừng các bạn</p>', 1, '2010-07-12 18:03:28'),
(3, 42, 'test sender', '<p>test message</p>', 1, '2010-07-12 19:23:31'),
(4, 42, 'test sender', '<p>Test sender again</p>', 1, '2010-07-12 19:24:56'),
(5, 42, 'phuc', '<p>xin chào<br />Tôi là...<br />Chúc mừng hạnh phúc</p>', 1, '2010-08-03 00:01:27'),
(6, 42, 'test', '<p>test</p>', 1, '2010-08-26 22:30:23'),
(7, 42, 'test again', '<p>blah blah blah blah blah</p>', 1, '2010-08-26 22:38:49'),
(13, 54, 'Lê Đình Sơn - ITK7', 'Chúc 2 bạn nhanh có thằng cu (hoặc con hĩm càng tốt) cho ông bà bế nhé! (Mà nhớ sau khi đám cưới xong thì mua vàng ít thôi. Phải để dành thật nhiều tiền mà mua bỉm. Nhớ đấy. Hì...)', 1, '2010-09-21 21:14:39'),
(14, 54, 'An', '<p>Chúc mừng đám cưới của anh chị ^_^. Đố biết là ai đấy.</p>', 1, '2010-09-24 23:06:19'),
(15, 54, 'Chị Hằng', '<p>Chị rất muốn về để chúc mừng hạnh phúc 2 em, nhưng không thể bố trí được vì tối nào cũng phải đi học. Em thông cảm cho chị nhé. Chúc em hạnh phúc, mọi việc luôn tốt đẹp!</p>', 1, '2010-09-25 06:56:33'),
(16, 54, 'Đào Đức Hạnh', '<p>Chúc anh chị mãi mãi hạnh phúc và đạt được nhiều mong ước! em rất tiếc không về chung vui cùng anh chị được. Chỉ mong anh biết là e luôn quan tâm đến anh dù ở xa anh.</p>', 1, '2010-09-25 06:59:53'),
(17, 54, 'Trương Minh Trực - 12E', '<p>Chúc mừng hạnh phúc hai bạn, chúc hai bạn mãi mãi hạnh phúc, đợt này cuối tháng, nhiều việc nên mình không về được, chân thành xin lỗi hai bạn, mình về uống rượu phát sau nhé!</p>', 1, '2010-09-25 07:01:36'),
(18, 54, 'Em Nhung', '<p>Anh ơi, trẻ con hôm nay đã cưới vợ nhỉ??? Hihi...</p>\r\n<p>Em chúc anh chị mãi mãi hạnh phúc nhé. ^^</p>\r\n<p>Trông ảnh cưới hay lắm cơ :D</p>', 1, '2010-09-26 07:05:15'),
(20, 54, 'Mai Phuong Thao', '<p>To chuc 2 kau mai mai hanh phuc. To cung pun khj kau hok nho moi to di du dam cuoi of kau nhung hok sao thay kau hp to cung dzui lay. To hy vong myk cung som dung chan de dc hp hok fai chay kiem tim 1 tinh iu nao nua</p>', 1, '2010-09-27 14:57:59'),
(21, 54, 'nguyenthanhhuy2008', '<p>Hạnh phúc trăm năm anh chị. Album ảnh đẹp quá anh chi ơi hi hi</p>', 1, '2010-09-27 15:03:08'),
(22, 54, 'Mr Trường ', '<p>Trông ảnh cưới bố cháu chụp ở đâu mà đẹp thế</p>\r\n<p>Chúc mừng chúc mừng nhé !!!</p>', 1, '2010-09-27 17:14:36'),
(23, 54, 'lkhl2003', '<p>Well done! :D</p>', 1, '2010-09-28 11:28:46'),
(24, 54, '', '<p>ảnh của cô chú đẹp thật đấy!!! chúc cô chú hạnh phúc</p>', 1, '2010-09-28 11:49:29'),
(25, 92, 'Kim Dung', '<p>Bùm chéo! ^_^<br />Chúc hai người trăm năm hạnh phúc.</p>', 1, '2010-09-30 17:06:47'),
(26, 92, 'Lương Hiền', '<p>Ảnh rất đẹp.<br />Chúc hai người trăm năm hạnh phúc nhé!<br />Àh quên mất. Hiền góp ý nhỏ hiền mở mục câu chuyện tình yêu của tuấn chưa có gì đề nghị bổ sung vào nhé. hiiiiiiiiiiiiiiiiiiiiiiii</p>', 1, '2010-10-01 08:03:35'),
(27, 92, 'E Thùy nè !', '<p>hai người đẹp đôi wa ah !<br />vào xem ảnh hai ngươiì đẹp wa.<br /><em><strong>E chúc hai người trăm năm hạnh phúc !<br />:d</strong></em></p>', 1, '2010-10-01 09:50:35'),
(28, 92, 'E Thùy nè !', '<p>Ah e e wen mất.<br />Trang web của anh e vào video mà hok dc a cho video vào cho sinh động nhé</p>', 1, '2010-10-01 09:53:02'),
(29, 54, 'thuy an', '<p>chuc 2 vo chong nhanh chong sinh dc thang cu ti noi doi tong duog</p>', 1, '2010-10-01 20:12:10'),
(30, 54, 'Cô chủ nhỏ!!', '<p>hi! nhìn ảnh cưới của Phúc xinh khác qá. chẳng nhận ra là Phúc xinh cả hiii. thích nhờ. thế là phúc xinh đã bị tù trung thân. Ko có giảm án đâu Phúc àh! =))</p>', 1, '2010-10-01 23:27:00'),
(31, 42, 'rwxtiacrfa', '8rX4YY  <a href="http://vplkdmhhvayh.com/">vplkdmhhvayh</a>, [url=http://gqwniebqzkoy.com/]gqwniebqzkoy[/url], [link=http://fvquhkqvnmzb.com/]fvquhkqvnmzb[/link], http://bxtnydrkjmle.com/', 1, '2010-10-02 03:01:54'),
(35, 92, 'lộc thảo ', '<p> anh loc thao chuc mung hanh phuc 2 em nhe ! hik</p>', 1, '2010-10-03 15:14:54'),
(45, 91, 'Dũng Văn hoá Yên Định', '<p>Đẹp đấy bác à. Cô dâu xinh thế sao ko giới thiệu cho em. He he!</p>', 1, '2010-10-05 14:57:33'),
(46, 92, 'Ngoc Oanh', '<p>He he ''''<br />Hanh phuc tram nam ^_^</p>', 1, '2010-10-05 14:57:46'),
(47, 91, 'Dũng Văn hoá Yên Định', '<p>à quên!<br />Chúc cho anh chị......Trăm năm hạnh phúc.......................</p>', 1, '2010-10-05 14:58:57'),
(48, 91, 'Thanh vien xom ca sau chua', '<p>Ảnh cưới đẹp lém c a! He không ngờ chị Thuy xom m lại xuất chuồng đầu tiên ở xóm cá sấu. Hj! Chúc Anh Chị trăm năm hạnh phúc, mãi yêu nhau như lúc đầu và sớm có những đứa trẻ tuyệt vời..... Nhớ Chị và mọi người ở xóm quá. HM la em</p>', 1, '2010-10-07 14:53:26'),
(49, 92, 'Nguyễn Tấn Dũng - TTg Nước CHXHCN Việt Nam', '<p>Chú chúc mừng hạnh phúc hai cháu! Chú rất bận, đang tham gia hội nghị thượng đỉnh các nước Châu Á, Thái Bình Dương về vấn đề bảo vệ toàn vẹn lãnh thổ. Bọn Trung Cộng đang âm mưu chiếm vịnh Bắc Bộ của nước ta, chú lần này sẽ làm mạnh tay, chú sẽ bắn cho bọn nó vài loạt ToMaHốc coi như đây là loạt pháo mừng hạnh phúc hai cháu. Vừa giữ được nước vừa trọn việc nhà với hai cháu! Một lần nữa chú thay mặt chính phủ chúc hai cháu hạnh phúc!<br />                                            Thân ái!<br />                                             (Đã ký)<br />                                         Nguyễn Tấn Dũng</p>', 0, '2010-10-08 10:51:16'),
(33, 92, 'Em Lợi', '<p>chúc mừng hai anh chị trăm năm hạnh phúc nha? trong đẹp đôi lắm?</p>', 1, '2010-10-02 17:23:07'),
(34, 92, 'Anh Phúc', '<p>Chúc hai em mãi mãi hạnh phúc nhé.</p>', 1, '2010-10-02 21:29:34'),
(40, 91, 'lacaixanh', '<p>2 nguoi dep doi wua troi ne chuc 2 nguoi tram nam hanh phuc sinh con trai dau long nha ,co dau xinh oi la xinh chu re cung dc</p>', 1, '2010-10-03 18:40:06'),
(41, 91, 'Thành viên nhóm cá sấu', '<p>E chúc mừng hạnh phúc của anh chị nhé! chúc anh chị trăm năm hạnh phúc, ... chị xinh quá đi, e cho đồng nghiệp của e xem nó bảo chị giống Cẩm ly, còn xinh hơn cẩm ly nữa chứ. haha. vui quá</p>', 1, '2010-10-04 12:24:25'),
(50, 91, 'Lương Hiền', '<p>Ảnh rất đẹp.<br />Chúc anh chị trăm năm hạnh phúc nhé.<br />Àh đề nghị chú rể đi chỉnh lại bộ răng đen đi nhé. hiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiiii</p>', 1, '2010-10-08 15:16:20'),
(51, 91, 'Akay p42 tho xuan', '<p>Bất ngờ qua chị ơi! chúc mừng hạnh phúc cua anh chị nhé! ảnh đẹp quá. xóm cá sấu mà có ai xấu đâu. hi hi.</p>', 1, '2010-10-08 16:43:28'),
(52, 54, 'học trò của cô', '<p>Em chúc cô chú mãi mãi hạnh phúc. Chúc cô chú nhanh chóng thực hiện được "kế hoạch" của mình. Hehe >)</p>', 1, '2010-10-08 22:31:29'),
(53, 42, 'Isaac', 'Google <a href="http://google.com">google</a>', 1, '2010-10-08 22:46:07'),
