INSERT INTO `jos_wedding_home` (`id`, `user_id`, `title`, `intro`, `content`, `image`) VALUES
(1, 42, 'Đám cưới', '<p>Chào mừng các bạn đến với website đám cưới của chúng tôi</p>\r\n<p>Thêm cái gì đấy dài dài dài vào</p>', '<p>Chào mừng các bạn đến với website đám cưới của <em><strong>chúng tôi<br /></strong></em></p>\r\n<p>Thêm cái gì đó dài</p>\r\n<p>dài</p>\r\n<p>dài</p>\r\n<p>dài nữa</p>\r\n<p>dài nữa</p>\r\n<p>dài thêm tẹo nữa</p>\r\n<p>thôi nhỉ?</p>', 'images/wedding/home/home-42.jpg'),
(2, 54, 'Chào mừng các bạn đến với website đám cưới Phúc Trang', '', '<p style="text-align: left;">Có câu danh ngôn thế này:</p>\r\n<p style="text-align: center;"><strong>"Cuộc sống có 3 điều hạnh phúc: có việc gì đó để làm, có ai đó để yêu thương, có một điều gì đó để hi vọng"</strong></p>\r\n<p>Nhưng ai đó bảo:</p>\r\n<p>[+] Có việc gì đó để làm: nhưng luôn muốn thay đổi.</p>\r\n<p>[+] Có ai đó để yêu thương: nhưng vẫn chưa muốn cưới.</p>\r\n<p>[+] Có một điều gì đó để hi vọng: không! có nhiều điều để hi vọng.</p>\r\n<p>Đừng tìm điều gì đó tuyệt đối bạn nhé, hãy để Yêu thương dẫn dường cho Hi vọng, cho trái tim 2 người mãi mãi đập cùng nhau.</p>\r\n<p> </p>\r\n<hr />\r\n<p><strong>Hôm nay lang thang trên mạng, kiếm được bài thơ hay, tặng cả nhà cùng thưởng thức:</strong></p>\r\n<p>Em gái tuổi Dần duyên phận long đong<br />Miệng đời bảo: cầm tinh em, cọp cái<br />Thầy bói nhủ: cái tuổi này cao số<br />Quê thấy mồ, em có ăn thịt ai đâu! <br /><br />Cha mẹ sinh năm Dần, em để vậy, biết sao<br />Em vẫn rộn những niềm vui không rõ!<br />Em dễ khóc những nỗi buồn vô cớ<br />Như sáng mai nào đang nắng, thoắt mưa... <br /><br />Em tuổi Dần, đã sợ em chưa?<br />Quen, quen vậy, anh khoan thưa bố mẹ!<br />Tự ái đấy khi bà cô bắt bẻ:<br />"Nó tuổi Dần, không ích tử, vượng phu..." <br /><br />Nếu mai sau, mai sau nữa, cho dù<br />Tờ âm lịch lỗi thời thay đổi hết<br />Thì chớ tưởng gái tuổi Dần đã thích<br />Con gái mà, không coi bói đâu vui! <br /><br />Em tuổi Dần nên người yêu em ơi,<br />Anh phải đẹp như chàng trai săn hổ<br />Lúc ấy, bên anh, em hiền như con mèo nhỏ<br />Vuốt nanh nào thi thố với tình yêu!<br /><br />Và anh yêu, xin anh chớ có liều,<br />Chớ bay bướm, đèo bòng năm bảy mối!<br />Cơn ghen gái tuổi Dần không dữ dội,<br />Chỉ diễn có một lần, kẻ có tội....tan xương!</p>\r\n<p><strong>(Đọc xong chẳng thấy SỢ, hi)</strong></p>', 'images/wedding/home/home-54.jpg'),
(3, 91, 'Chào mừng các bạn đến với website đám cưới Quân Thủy', '<p>Hiện nay chúng tôi đang tiến hành cập nhật thông tin</p>\r\n<p>Mong các bạn dành thời gian quay lại để cổ vũ cho tình yêu của chúng tôi</p>\r\n<p>Trân trọng.</p>', '', 'images/wedding/home/home-91.jpg'),
(4, 92, '', '<p><strong></strong></p>', '<p style="text-align: center;"><strong><span style="color: #0000ff; font-family: Verdana;">TRÂN TRỌNG KÍNH MỜI CÁC BẠN <br />TỚI DỰ LỄ THÀNH HÔN CỦA HAI CHÚNG TÔI:<br /></span></strong><span style="font-size: small;"> <br />              <span style="color: #ff0000;"><strong>Nguyễn Anh Tuấn - Trịnh Phương Thảo</strong><br /></span></span></p>\r\n<div style="text-align: center;"><strong><span style="font-size: x-small; color: #0000ff;">Hôn lễ được tổ chức vào hồi 10h40'' ngày 16 tháng 10 năm 2010<br />(Tức là ngày 09 tháng 09 năm Canh Dần)<br /><br /></span></strong></div>\r\n<p style="text-align: center;"><strong><span style="font-size: x-small; color: #0000ff;">           Tại: Thôn 10 - Minh Tiến - Ngọc Lặc - Thanh Hoá<br /><br />    XIN TRÂN TRỌNG KÍNH MỜI<br /></span></strong></p>', 'images/wedding/home/home-92.jpg'),
(5, 76, '', '', '', 'images/wedding/home/home-76.jpg'),
(6, 104, '', '', '<p>                                      MINH YEU   % TRAN YEN</p>', 'images/wedding/home/home-104.jpg');
