INSERT INTO `jos_wedding_map_intro` (`id`, `user_id`, `intro`) VALUES
(1, 42, '<p>Nhà cô dâu và chú rể gần nhau (chỉ vài trăm mét thôi), nên cũng tiện, vẽ bản đồ chỉ cần một cái, là dùng được cho cả 2, hi. Mỗi đứa ngự trị một góc vuông, nên không có lựa chọn ra con đường ngắn nhất.</p>'),
(2, 54, '<p>chỉ đường về nhà cô dâu chú rể.</p>'),
(3, 92, '<p>Nhà mình ở xa, nên gửi bản đồ để mọi người tìm đường cho dễ nhé, nếu không tìm được cũng đừng quay về luôn, hi.</p>'),
(4, 104, '<p>02A PHO NGUYEN CHICH PHUONG NAM NGAN TPTH DEN NAM DINH</p>');
